dbConfig.query:
SELECT district_id, sy_from, fund_source_id, education_level_id, SUM(total) as total FROM school s, report_history rh, personnel_localfund_summary p WHERE s.id = rh.school_id AND p.report_history_id = rh.id AND rh.report_id IN (24, 26) AND sy_from >= @syFrom AND sy_from <= @syTo AND fund_source_id IN (94, 95, 251, 252, 253, 341) GROUP BY district_id, sy_from, fund_source_id, education_level_id
UNION
SELECT district_id, sy_from, 93 as fund_source_id, education_level_id, SUM(total) as total FROM school s, report_history rh, personnel_localfund_summary p WHERE s.id = rh.school_id AND p.report_history_id = rh.id AND rh.report_id IN (24, 26) AND sy_from >= @syFrom AND sy_from <= @syTo AND fund_source_id IN (92, 93) GROUP BY district_id, sy_from, fund_source_id, education_level_id;

SELECT district_id, sy_from, IF(report_id = 24, 433, 434) as education_level, item_id, sum(quantity) as count FROM school s, report_history rh, inventory_summary i WHERE s.id = rh.school_id AND i.report_history_id = rh.id AND rh.report_id in (24, 26) AND sy_from >= @syFrom AND sy_from <= @syTo
AND item_id IN (350, 351, 352, 353, 354, 357)
GROUP BY district_id, sy_from, education_level;
